import avatar from '../images/avatar.jpg';

function Main({ onEditAvatar, onEditProfile, onAddPlace }) {
    return (
        <main className="content">
            <section className="profile">
                <button type="button" className="profile__edit-avatar-btn" onClick={ () => document.querySelector('.profile__edit-avatar-btn').classList.add('popup_opened') }>
                    <img className="profile__avatar" src={avatar} alt="Аватар Жак-Ив Кусто" />
                </button>
                <div className="profile__info">
                    <div className="profile__bio">
                        <h1 className="profile__name"></h1>
                        <p className="profile__profession"></p>
                    </div>
                    <button type="button" className="profile__edit-btn profile__edit-btn-open-popup" onClick={onEditProfile}></button>
                </div>
                <button type="button" className="profile__add-btn profile__add-btn-open-popup" onClick={onAddPlace}></button>
            </section>
            <section className="elements" aria-label="галерея">
            </section>
        </main>
    );
}
export default Main;
