import React from "react";

function PopupWithForm(props) {
return(
    <div className={`popup popup_type_${props.name} ${props.isOpened ? 'popup_opened' : ''}`}> 
    <div className='popup__container'>
        <button type='button' className='popup__closed-btn'></button>
        <h3 className='popup__title'>{props.title}</h3>
        <form action='/' className='popup__form' name={props.name} noValidate>
        {props.children} 
            <button type='submit' className='popup__save-btn'>{props.nameBtnSave}</button>
        </form>
    </div>
</div>
);
}

export default PopupWithForm;
