import React from "react";

function PopupWithForm({card, children}) {
    return (
        <div className={`popup popup_type_${card.name} ${card.isOpen && "popup_opened"}`}>
            <div className="popup__container">
                <button className="popup__closed-btn" type="button" onClick={card.onClose}></button>
                <h3 className="popup__title">{card.title}</h3>
                <form className={`popup__form popup__form_${card.name}`} action="/" name={card.name} noValidate>
                    {children}
                    <button className="popup__save-btn" type="submit">{card.nameBtnSave || "Сохранить"}</button>
                </form>
            </div>
        </div>
    );
}

export default PopupWithForm;
