import { useState, useEffect } from 'react';
import api from '../utils/api.js';

function Main({ onEditAvatar, onEditProfile, onAddPlace }) {
    const [userName, setUserName] = useState('');
    const [userDescription, setUserDescription] = useState('');
    const [userAvatar, setUserAvatar] = useState('');
    const [cards, setCards] = useState([]);

    useEffect(() => {
        Promise.all([api.getUserData(), api.getCards()])
            .then(([userData, initialCards]) => {
                setUserName(userData.name);
                setUserDescription(userData.about);
                setUserAvatar(userData.avatar);
                setCards(initialCards);
                // defaultCardList.renderItems(initialCards.reverse());
            }).catch((err) => {
                console.log(err);
            });
    }, []);

    return (
        <main className='content'>
            <section className='profile'>
                <button type='button' className='profile__edit-avatar-btn' onClick={onEditAvatar}>
                    <img className='profile__avatar' src={userAvatar} alt='Аватар Жак-Ив Кусто' />
                </button>
                <div className='profile__info'>
                    <div className='profile__bio'>
                        <h1 className='profile__name'>{userName}</h1>
                        <p className='profile__profession'>{userDescription}</p>
                    </div>
                    <button type='button' className='profile__edit-btn profile__edit-btn-open-popup' onClick={onEditProfile}></button>
                </div>
                <button type='button' className='profile__add-btn profile__add-btn-open-popup' onClick={onAddPlace}></button>
            </section>
            <section className='elements' aria-label='галерея'>
            <article class="elements__element">
                <button type="button" class="elements__delete-btn"></button>
                <img class="elements__image" src="#" alt="" />
                    <div class="elements__container">
                        <h2 class="elements__title"></h2>
                        <div class="elements__container-likes">
                            <button type="button" class="elements__like-btn"></button>
                            <p class="elements__like-count"></p>
                        </div>
                    </div>
            </article>
            </section>
        </main>
    );
}
export default Main;
