function PopupWithForm() {
return(



    <div className={`popup popup_type_${props.name}`}>
    <div className='popup__container'>
        <button type='button' className='popup__closed-btn'></button>
        <h3 className='popup__title'>Обновить аватар</h3>
        <form action='/' className='popup__form popup__form_update-avatar' name='update-avatar-form' noValidate>
            <fieldset className='popup__form-items'>
                <input className='popup__input popup__input_type_avatar-link' type='url' name='avatar'
                    id='avatar-link-input' value='' placeholder='Ссылка на картинку' required='required' />
                <span className='popup__input-error avatar-link-input-error'></span>
            </fieldset>
            <button type='submit' className='popup__save-btn'>Сохранить</button>
        </form>
    </div>
</div>
);
}

export default PopupWithForm;
